context.value:
border-radius: 50px;
  padding: 1rem;
  background-color: var(--primary);
  color: var(--primary-text);
  display: block;
  cursor: pointer;
  font-weight: bold;
  font-size: 19px;

  &:hover,
  &:active {
    background-color: var(--primaryHover);
  }

  &.secondary {
    background-color: var(--base);
    border: 1px solid var(--lowestContrast);

    &:hover,
    &:active {
      background-color: var(--baseHover);
    }
  }
